    protected function reportError($message=null){
        $m = is_null($message) ? $this->errorMessage : $message;
        switch($this->reportLevel){
            case 0:
                //nothing
            break;

            case 1:
                echo '<br />'.$m."<br />";
            break;

            case 2:
                // $this->pdo = null;
                throw new Exception($m);
            break;

            default:
                $this->pdo = null;
                die('<br />'.$m."<br />");
        }
    }

